mapper.insert:

insert into schedule_job_log (log_id, job_id, executor_id, 
  exe_url, exe_interface, notify_start, 
  notify_end, finished, ret_msg, 
  notify_status, return_status, alert_times
  )
values (#{logId,jdbcType=BIGINT}, #{jobId,jdbcType=BIGINT}, #{executorId,jdbcType=BIGINT}, 
  #{exeUrl,jdbcType=VARCHAR}, #{exeInterface,jdbcType=VARCHAR}, #{notifyStart,jdbcType=BIGINT}, 
  #{notifyEnd,jdbcType=BIGINT}, #{finished,jdbcType=BIGINT}, #{retMsg,jdbcType=VARCHAR}, 
  #{notifyStatus,jdbcType=INTEGER}, #{returnStatus,jdbcType=INTEGER}, #{alertTimes,jdbcType=INTEGER}
  )

mapper.select:
select

from schedule_job_log
where job_id = #{jobId}

  AND return_status != 1

order by notify_start desc
limit #{offset}, #{pageSize}
